mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
FROM trip.member
WHERE id = #{id}

mapper.update:
update trip.member
set pw = #{pw}
where id = #{id}

UPDATE trip.member
SET name=#{name}, img_key=#{imgKey}
WHERE id=#{id}

UPDATE trip.member
SET pw=#{pw}
WHERE id=#{id}

mapper.select:
SELECT COUNT(*) > 0
FROM trip.member
WHERE id = #{id} AND pw = #{pw}

mapper.update:
UPDATE trip.member
SET member.rank = #{vipRank}
WHERE id = #{memberId}
